// Convert knowledge-base.json to knowledge-base.ts
const fs = require('fs');
const path = require('path');

const jsonPath = path.join(__dirname, '../public/knowledge-base.json');
const tsPath = path.join(__dirname, '../src/knowledge-base.ts');

// Read the JSON file
const jsonData = JSON.parse(fs.readFileSync(jsonPath, 'utf8'));

// Convert to TypeScript format
const tsContent = `// Knowledge Base for CFLAR Chatbot
// This file is auto-generated by the scraper (npm run scrape)

export const knowledgeBase = ${JSON.stringify(jsonData, null, 2)};

export default knowledgeBase;
`;

// Write the TypeScript file
fs.writeFileSync(tsPath, tsContent, 'utf8');

console.log(`âœ… Converted ${jsonData.pageCount} pages from JSON to TypeScript`);
console.log(`ðŸ“„ Output: ${tsPath}`);
